    "name": "sqlite_execute_query",
    "input": {
        "database_path": "/home/jvroig/Dev/sandbox/q402_s1/horse.db",
        "query": "SELECT COUNT(*) as num_big_orders FROM enterprise_orders INNER JOIN enterprise_customers ON enterprise_orders.CUST_REF = enterprise_customers.CUST_ID WHERE enterprise_customers.DEPT_CD = 'Engineering' AND enterprise_orders.ORD_AMT > 50000"
    }
}
